function isEnoughMoney(money, price, quantity, tax, coupon = 0) {
  const subtotal = price * quantity;
  const taxAmount = subtotal * (tax / 100);
  const couponAmount = subtotal * (coupon / 100);
  return money >= subtotal + taxAmount - couponAmount;
}

