function isAdmin(email, password) {
  if (email === 'admin@pasv.us' && password === '12345') {
    return true;
  }

  if (email === 'admin2@pasv.us' && password === '12345_') {
    return true;
  }

  if (email === 'admin3@pasv.us' && password === '12345__') {
    return true;
  }

  return false;

}

