function letterCount(str, letter) {
  if (letter === undefined) {
    return 'Wrong letter';
  }

  let count = 0;
  for (let i = 0; i < str.length; i++) {
    if (str[i].toLowerCase() === letter.toLowerCase()) {
      count++;
    }
  }
  return count;
}

