// use trimn and lastIndexOf
function isSentenceClosed(str) {
  const s = str.trim();
  return (
    s.lastIndexOf('.') === s.length - 1 ||
    s.lastIndexOf('!') === s.length - 1 ||
    s.lastIndexOf('?') === s.length - 1
  );
}

