function createUser(firstName, lastName, age, city, email, phone) {
  let p = phone.trim();
  if (p[0] !== '+') p = '+' + p;

  return {
    name: `${firstName.trim()} ${lastName.trim()}`,
    age,
    city: city.trim(),
    email: email.trim().toLowerCase(),
    phone: p,
  };
}

