function carCreate(brand, model, year, color) {
  const b = brand.trim();
  const m = model.trim();
  const c = color.trim().toLowerCase();

  return {
    brand: b,
    model: m,
    year,
    color: c,
    code: (b[0] + m[0] + year + c[0]).toUpperCase(),
  };
}

