// функция для расчета среднего арифметического из семи значений

function average(num1, num2, num3, num4, num5, num6, num7) {
  return (num1 + num2 + num3 + num4 + num5 + num6 + num7) / 7;
}

openTest:
it('Created function average', () => {
  expect(average).to.be.a('function');
});

it('average(1, 2, 3, 4, 5, 6, 7) should return 4', () => {
  expect(average(1, 2, 3, 4, 5, 6, 7)).to.equal(4);
});

it('average(10, 15, 20, 25, 30, 35, 40) should return 25', () => {
  expect(average(10, 15, 20, 25, 30, 35, 40)).to.equal(25);
});

it('average(100, 200, 300, 400, 500, 600, 700) should return 400', () => {
  expect(average(100, 200, 300, 400, 500, 600, 700)).to.equal(400);
});

// negetive values
it('average(-1, -2, -3, -4, -5, -6, -7) should return -4', () => {
  expect(average(-1, -2, -3, -4, -5, -6, -7)).to.equal(-4);
});
